const fs = require('fs');
const util = require('util');
const snippets = require('../snippets');

module.exports = function(RED) {
    function LogisticRegression(config) {
        RED.nodes.createNode(this,config);
        var node = this;
        var penalty = config.penalty;
        var solver = config.solver;
        var script = '';
        node.on('input', function(msg) {
            script += util.format(snippets.LOGISTICREGRESSION, penalty, solver);
            msg.payload += script;
            node.send(msg);
                 
        });
    }
    RED.nodes.registerType("logisticregression", LogisticRegression);
}






