
var util = require("util");
var httpclient;
var PythonshellNode = require('./deploynode');

module.exports = function(RED) {
  "use strict";

  function PythonshellInNode(n) {
    RED.nodes.createNode(this,n);

    var node = this;
    node.config = n; // copy config to the backend so that down bellow we can have a reference

    var pyNode = new PythonshellNode(n);

    pyNode.setStatusCallback(node.status.bind(node))
  
    node.on("input",function(msg) {
      pyNode.onInput(msg, function(result){
        node.send(result);
      }, function(err){
        node.error(err);
      });
    });

    node.on('close', ()=>pyNode.onClose());
  }

  RED.nodes.registerType("deploy", PythonshellInNode);

  RED.httpAdmin.post("/deploy/:id", RED.auth.needsPermission("deploy.query"), function(req,res) {
    var node = RED.nodes.getNode(req.params.id);
    if (node != null) {
      try {
        if (node.config.continuous){// see above comment
          node.receive({payload: 'pythonshell@close'})
        } else {
          node.receive();
        }
        res.sendStatus(200);
      } catch(err) {
          res.sendStatus(500);
          node.error(RED._("deploy.failed",{error:err.toString()}));
      }
    } else {
        res.sendStatus(404);
    }
  });

}